/**
 * <p>
 * 	The data access object for ${BeanName} objects (rows of table
 *  ${TableName}).
 * </p>
 * @see {@link ${BeanName}DAO}
 */
@Stateless
public class ${BeanName}DAOBean implements ${BeanName}DAO {
	
	private final static Logger LOG = Logger.getLogger(${BeanName}DAOBean.class);

	// Generic HQL request to find instances of ${BeanName} by pk
	// TODO choose between left/inner join
	private static final String FIND_BY_PK(boolean fetchLink1, boolean fetchLink2) {
		return "from ${BeanName}VO vo "
				+ (fetchLink1 ? "<inner|left> join fetch vo.link1 " : "")
 			    + (fetchLink2 ? "<inner|left> join fetch vo.link2 " : "")
				+ "where vo.pk = :pk";
	}

	// Generic HQL request to find all instances of ${BeanName}
	// TODO choose between left/inner join
	private static final String FIND_ALL(boolean fetchLink1, boolean fetchLink2) {
		return "from ${BeanName}VO vo "
				+ (fetchLink1 ? "<inner|left> join fetch vo.link1 " : "")
 			    + (fetchLink2 ? "<inner|left> join fetch vo.link2 " : "");
	}

	@PersistenceContext
	private EntityManager entityManager;

	/* Creation/Update methods ---------------------------------------------- */

	/**
	 * <p>
	 *  Insert the given value object.
	 * 	TODO update this comment for insertion details.
	 * </p>
     */
	public void create(${BeanName}VO vo) throws Exception {
		this.checkAndCompleteData(vo, true);
		this.entityManager.persist(vo);
	}

	/**
	 * <p>
	 *  Update the given value object.
	 * 	TODO update this comment for update details.
	 * </p>
     */
	public ${BeanName}VO update(${BeanName}VO vo) throws Exception {
		this.checkAndCompleteData(vo, false);
		return entityManager.merge(vo);
	}

	/* Deletion methods ----------------------------------------------------- */

	/**
	 * <p>
	 * 	Deletes the given value object.
	 * </p>
	 * @param vo the value object to delete.
     */
	public void delete(${BeanName}VO vo) {
		entityManager.remove(vo);
	}

	/* Find methods --------------------------------------------------------- */

	/**
	 * <p>
	 * 	Returns the ${BeanName}VO instance matching the given primary key.
	 * </p>
	 * <p>
	 *  <u>Please note</u> that the booleans to fetch relationships are needed <u>ONLY</u>
	 *  if the value object has to be used out the EJB container.
	 * </p>
	 * @param pk the primary key of the object to load.
	 * @param fetchRelation1 if true, the linked instances by the relation "relation1" will be set.
	 * @param fetchRelation2 if true, the linked instances by the relation "relation2" will be set.
     */
	public ${BeanName}VO findByPk(Integer pk, boolean fetchRelation1, boolean fetchRelation2) {
		try{
			return (${BeanName}VO) entityManager.createQuery(
				FIND_BY_PK(fetchRelation1, fetchRelation2)).setParameter("pk", pk).getSingleResult();
		}catch(NoResultException e){
			return null;
		}
	}
	
	/**
	 * <p>
	 * 	Returns the ${BeanName}VO instances.
	 * </p>
	 * <p>
	 *  <u>Please note</u> that the booleans to fetch relationships are needed <u>ONLY</u>
	 *  if the value object has to be used out the EJB container.
	 * </p>
	 * @param fetchRelation1 if true, the linked instances by the relation "relation1" will be set.
	 * @param fetchRelation2 if true, the linked instances by the relation "relation2" will be set.
     */
	@SuppressWarnings("unchecked")
	public List<${BeanName}VO> findAll(boolean fetchRelation1, boolean fetchRelation2) {
		return (List<${BeanName}VO>) entityManager.createQuery(
			FIND_ALL(fetchRelation1, fetchRelation2)).getResultList();
	}

	@SuppressWarnings("unchecked")
	public List<${BeanName}VO> findFiltered(Integer param1, String param2) {

		Session session = (Session) this.entityManager.getDelegate();

		Criteria crit = session.createCriteria(${BeanName}VO.class);

		crit.setResultTransformer(Criteria.DISTINCT_ROOT_ENTITY); // DISTINCT RESULTS !

		if (param2 != null && !param2.isEmpty()) {
			crit.add(Restrictions.ilike("param2", param2));
		}

		if (param1 != null) {
			crit.add(Restrictions.eq("param1", param1));
		}
		crit.addOrder(Order.desc("param1"));

		return crit.list();
	}

	/* Private methods ------------------------------------------------------ */

	/**
	 * Checks the data for integrity. E.g. if references and categories exist.
	 * @param vo the data to check
	 * @param create should be true if the value object is just being created in the DB, this avoids some checks like testing the primary key
	 * @exception VOValidateException if data is not correct
	 */
	private void checkAndCompleteData(${BeanName}VO vo, boolean create) throws Exception {
		
		if(create) {
			if(vo.getPk() != null) {
				throw new IllegalArgumentException("Primary key is already set! This must be done automatically. Please, set it to null!");
			}
		}
		else {
			if(vo.getPk() == null) {
				throw new IllegalArgumentException("Primary key is not set for update!");
			}
		}
		// check value object
		vo.checkValues(create);
		// TODO check primary keys for existence in DB
	}
}

